import React, { useState } from 'react';

const Login = (props) => {
  const [userName, setUserName] = useState("")
  const handleInputUserName = (name) => {
    setUserName(name);
  }
  return (
    <div>
      <input
        name="nameText"
        type="text"
        value={ userName }
      />
      <button type="button" onClick={() => handleInputUserName()}>Send</button>
    </div>);
}

export default Login;
