class ActionProvider {
    constructor(
        createChatBotMessage,
        setStateFunc,
        createClientMessage,
        stateRef,
        createCustomMessage,
        ...rest
    ) {
        this.createChatBotMessage = createChatBotMessage;
        this.setState = setStateFunc;
        this.createClientMessage = createClientMessage;
        this.stateRef = stateRef;
        this.createCustomMessage = createCustomMessage;
    }

    greet() {
        const greetingMessage = this.createChatBotMessage("Please, tell me your username.")
        this.updateChatbotState(greetingMessage)
    }
    /*
    getUserName (message, state) {
        this.updateChatbotState(message)
    }
    */
    goodBye() {
        const goodByeMessage = this.createChatBotMessage("Goodbye!")
        this.updateChatbotState(goodByeMessage)
    }

    loanOptions() {
        console.log("teste");
        const message = this.createChatBotMessage("Loan informations:",
            {
                widget: "loanLinks",
            }
        );
        this.updateChatbotState(message);
    };

    updateChatbotState(message) {
        this.setState(prevState => ({
            ...prevState, messages: [...prevState.messages, message]
        }))
    }
}

export default ActionProvider;
